package _05_Booleans;

public class _01_booleans {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean open = false;
		boolean locked = true;
		System.out.println(locked);
		
		boolean tired = true;
		boolean awake = false;
		System.out.println(awake);
		
		//Make your own boolean statement print as false
		
		boolean phoneOn = true;
		boolean phoneOff = false;
		System.out.println(phoneOff);
		
		//Numbers
		
		System.out.println(1>5);
		
		//Naming conventions
		
		boolean isRunning = false;
		boolean isLoggedIn = true;
		
		if(isRunning == true) {
			System.out.println("Thank god that car is still running!");	
		} else {
			System.out.println("Get yourself a new Car!");
		}
		
		
		
		
		

	}

}
